<script>

    $(document).ready(function(){
        $('.article-content p').each(function(){
            var paragraphClass = $(this).attr('class');
            if(!isNaN(paragraphClass)){
                $(this).attr('id', paragraphClass);
            }
        });
        $('.nofancybox a').removeAttr('data-fancybox');

        $('table.has-fixed-layout').addClass('wp-block-table');

        function mapTouchEvents(event,simulatedType) {
            if(event.changedTouches.length>1){return;}
            var touch = event.changedTouches[0];
            eventToSimulate = document.createEvent('MouseEvent');
            eventToSimulate.initMouseEvent(
                simulatedType,		//type
                true,				//bubbles
                true,				//cancelable
                window,				//view
                1,					//detail
                touch.screenX,		//screenX
                touch.screenY,		//screenY
                touch.clientX,		//clientX
                touch.clientY,		//clientY
                false,				//ctrlKey
                false,				//altKey
                false,				//shiftKey
                false,				//metaKey
                0,					//button
                null				//relatedTarget
            );

            touch.target.dispatchEvent(eventToSimulate);
            event.preventDefault();

        }

        const elementToScroll = document.querySelectorAll('.horizontal > .simplebar-scrollbar');
        elementToScroll.forEach(item =>{
            item.addEventListener('touchstart',function(e){ mapTouchEvents(e,'mousedown'); },false);
        });
        elementToScroll.forEach(item =>{
            item.addEventListener('touchmove',function(e){ mapTouchEvents(e,'mousemove'); },false);
        });
        elementToScroll.forEach(item =>{
            item.addEventListener('touchend',function(e){ mapTouchEvents(e,'mouseup'); },false);
        });
        elementToScroll.forEach(item =>{
            item.addEventListener('touchcancel',function(e){ mapTouchEvents(e,'mouseup'); },false);
        });

        $('table>img').each(function(){
            var imageLink = $(this).attr('src');
            $(this).wrap('<a href="'+ imageLink +'" data-fancybox data-type="image"></a>');
        });
        $('.main-banner').removeAttr('data-fancybox');
        $('ol').children('li').each(function(i,elem){
            i++;
            $(this).children('ol').addClass('child-list');
            $(this).children('.child-list').children('li').each(function(x,elem){
                x++;
                $(this).append('<span class="child-list__counter">'+ i + '.' + x +'</span>');
            });
        });
        $('.wp-block-cgb-gray-background-block br').before('<div></div>');
    });

    $(document).ready(function(){
        if($(window).width() > 767){
            $(document).on('scroll', function(){
                if(!$('.article-content-menu').hasClass('is_stuck')){
                    $('.article-content-menu').removeClass('opened');
                }
            });
            $('.topper-menu a').on('click', function (event) {
                event.preventDefault();

                $('html, body').animate({
                    scrollTop: $($.attr(this, 'href')).offset().top
                }, 500);
            });

            $('.topper-menu a').each(function(){
                $(this).attr('target', '_self');
            });

        }
    });
    /* Скрывать/показать содржание в верхней части, в зависимости от высоты*/
    $(document).ready(function(){
        if($(window).width() > 1024){
            if($('.topper-menu').hasClass('hidden')){
                var headerHeight = $('.articles-list-item_title').height();
                var tableOfContentsHeight = 548 - headerHeight;
                $('.topper-menu').css('height', '' + tableOfContentsHeight +'');
                $('.article-content-wrapper .article-content-menu').css('clear', 'both');
                $('.article-content-wrapper .article-content-menu').next().css('padding-top', '25px');
                $('.article-content p, .article-content li, .article-content h2, .article-content h3, .article-content h4, .social-banner, .tooligram, .article-banner, .wp-block-quote').each(	function(){
                    $(this).css({ "margin-left":"auto", "margin-right":"auto", "display":"block !important", "clear":"both"});
                });
            }
            $('.topper-menu .menu-opener').on('click', function(){
                $('.topper-menu').toggleClass('hidden');
                $('.topper-menu').toggleClass('opened');

                if($('.topper-menu').hasClass('hidden')){
                    $('.topper-menu.hidden').attr('style', 'height: ' + tableOfContentsHeight + 'px' + ' !important');
                    $('.topper-menu .menu-opener').text('Показать всё содержание');
                }else{
                    $('.topper-menu.opened').css('height', $('.topper-menu__items').height() + 50);
                    $('.topper-menu .menu-opener').text('Скрыть содержание');
                }
            });

            /* Magic begins, top text stands to left, else text stands to center */
            $('.article-content p, .article-content li, .article-content h2, .article-content h3, .article-content h4, .social-banner, .tooligram, .article-banner, .wp-block-quote').each(function(){
                var mainBannerOffset = $('.main-banner').offset().top + 605;
                var elemOffset = $(this).offset().top;
                if(elemOffset > mainBannerOffset){
                    $(this).css({ "margin-left":"auto", "margin-right":"auto", "display":"block", "clear":"both"});
                }else{
                    $(this).css({ 'margin-right':'auto'});
                }
            });
            $('.wp-block-table').each(function(){
                if($(this).width() > 940){
                    $(this).css('width', 'auto');

                    $(this).parent().addClass('dragscroll');
                    $('.simplebar-content').mousedown(function(){
                        $('body').addClass('mouseon');
                    });
                    $(document).mouseup(function(){
                        $('body').removeClass('mouseon');
                    });
                }else{
                    $(this).css('width', '100%');
                }
            });
        }
    });

    $(document).ready(function(){
