            /* Magic begins, top text stands to left, else text stands to center */
            $('.article-content p, .article-content li, .article-content h2, .article-content h3, .article-content h4, .article-banner, .tooligram, .article-banner, .wp-block-quote').each(function(){
                var mainBannerOffset = $('.main-banner').offset().top + 160;
